import React, { useState, useEffect } from "react";
import "./Weather.css";

function Weather() {
  const [city, setCity] = useState(null);
  const [search, setSearch] = useState("");
  useEffect(() => {
    const fetchApi = async () => {
      const url = `http://api.openweathermap.org/data/2.5/weather?q=${search}&appid=a9e3a8b240494d66a0304a42fc2de81f`;
      const response = await fetch(url);
      const respJson = await response.json();
      setCity(respJson);
    };
    fetchApi();
  }, [search]);

  return (
    <div className="main-box">
      <div className="box">
        <div className="search">
          <input
            type="search"
            placeholder="search"
            onChange={(event) => {
              setSearch(event.target.value);
            }}
          />
        </div>
        {!city ? (
          <p>No Data Found</p>
        ) : (
          <div>
            <div className="city">
              <h2>
                <i className="fas fa-street-view"></i>
              </h2>
              <h2>{search}</h2>
            </div>
            <div className="temp">
              <h2>
                {city.main.temp}<span>&#8451;</span>
              </h2>
              <p>
                MIN: 23<span>&#8451;</span>|MAX: 28<span>&#8451;</span>
              </p>
            </div>
            <div className="temps">
              <h2>The last 4 day temperature is:</h2>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default Weather;
